mapper.insert:
insert into perm (perm_id, perm_url, perm_description
  )
values (#{permId,jdbcType=INTEGER}, #{permUrl,jdbcType=VARCHAR}, #{permDescription,jdbcType=VARCHAR}
  )

mapper.update:
update perm
set perm_url = #{permUrl,jdbcType=VARCHAR},
  perm_description = #{permDescription,jdbcType=VARCHAR}
where perm_id = #{permId,jdbcType=INTEGER}

mapper.select:
SELECT p.* FROM perm AS p
                  INNER JOIN role_perm AS rp ON p.perm_id = rp.perm_id
                  INNER JOIN user_role AS ur ON ur.role_id = rp.role_id
WHERE ur.user_id=#{userId}
GROUP BY p.id ORDER BY p.id ASC;
